mapper.select:
    select 
    
    from ph_dict_item
    where 1=1
		
   			 AND dict_id = #{dictId}
  		 
		
   			 AND dict_type = #{dictType}
  		 
		
   			 AND dict_item_code = #{dictItemCode}
  		 
		
   			 AND dict_item_name = #{dictItemName}
  		 
		
   			 AND show_order = #{showOrder}
  		 
		
   			 AND item_status = #{itemStatus}
  		 
		
   			 AND remark = #{remark}
  		 
	  	
   			 order by ${sidx} ${order}
  		 
  		
   			 limit ${startRow},${limit}

    select 
     count(*)
    from ph_dict_item
    where 1=1
		
   			 AND dict_id = #{dictId}
  		 
		
   			 AND dict_type = #{dictType}
  		 
		
   			 AND dict_item_code = #{dictItemCode}
  		 
		
   			 AND dict_item_name = #{dictItemName}
  		 
		
   			 AND show_order = #{showOrder}
  		 
		
   			 AND item_status = #{itemStatus}
  		 
		
   			 AND remark = #{remark}
