mapper.select:
    select 
    
    from ph_report_data
    where 1=1
		
   			 AND EXAM_ID = #{examId}
  		 
		
   			 AND EXAM_DATA = #{examData}
  		 
		
   			 AND CREATE_TIME = #{createTime}
  		 
	  	
   			 order by ${sidx} ${order}
  		 
  		
   			 limit ${startRow},${limit}

    select 
     count(*)
    from ph_report_data
    where 1=1
		
   			 AND EXAM_ID = #{examId}
  		 
		
   			 AND EXAM_DATA = #{examData}
  		 
		
   			 AND CREATE_TIME = #{createTime}
